var utility={},settingsPage={};settingsPage.DataTableAdd=function(n,t){$(document).ready(function(){var i=$(n).DataTable({serverSide:!0,ajax:function(n,r){t.invokeMethodAsync("LoadData",n).then(n=>{r(n),i.rows("[sel=1]").select()})},columns:[{data:"StockID"},{data:"StockNumber"},{data:"Inventory"},{data:"Description"},{data:"Location"},{data:"Manufacturer"},{data:"Vendor"}],select:{style:"multi"}});i.on("select",function(n,i,r,u){if(r==="row"){var f=i.rows(u).data()[0];t.invokeMethodAsync("AddStock",f.StockID)}});i.on("deselect",function(n,i,r,u){if(r==="row"){var f=i.rows(u).data()[0];t.invokeMethodAsync("RemoveStock",f.StockID)}})})};utility.DataTablesAdd=function(n){$(document).ready(function(){$(n).DataTable()})};utility.DataTablesRemove=function(n){$(document).ready(function(){$(n).DataTable().destroy();try{var t=document.querySelector(n+"_wrapper");t.parentNode.removeChild(t)}catch(i){}})};